import React from 'react';
import styled from 'styled-components';
import {useState} from 'react';

const Container = styled.div`
display: flex;
justify-content: center;
align-items: center;
height: 100vh;
background-color: #fce4ec; /* Pink background color */
`;

const Form = styled.form`
display: flex;
flex-direction: column;
align-items: center;
padding: 2rem;
background-color: #fff;
border-radius: 0.5rem;
box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.3);
`;

const Title = styled.h1`
font-size: 2rem;
margin-bottom: 2rem;
`;

const Label = styled.label`
font-size: 1.2rem;
margin-bottom: 1rem;
`;

const Input = styled.input`
font-size: 1.2rem;
padding: 0.5rem;
margin-bottom: 2rem;
border: none;
border-bottom: 2px solid #555;
outline: none;
&:focus {
border-color: #e91e63; /* Pink border color on focus */
}
`;

const Button = styled.button`
font-size: 1.2rem;
padding: 0.5rem 2rem;
background-color: #e91e63; /* Pink button color */
color: #fff;
border: none;
border-radius: 0.5rem;
cursor: pointer;
&:hover {
background-color: #ff69b4; /* Lighter pink button color on hover */
}
`;

const fields = [
  {label: "Email Address", type:"text", placeholder:"Input Email"},
  {label: "Password", type:"password", placeholder:"Input Password"}
];

const Newpage = () => {
  const [email, setEmailAddrs] = useState('');
//const[password, setPassword] = useState('');
return (
    <Container>
      <Form>
        {fields.map((field) => {
            return(
                <Form.group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>{field.label}</Form.Label>
                    <Form.control type={field.type} placeholder={field.placeholder}
                    onChange={(e) => setEmailAddrs(e.target.value)}/>
                </Form.group>
            )
        })}
        <Title>Login</Title>
        <Label htmlFor="email">Email</Label>
        <Input type="text" id="email" name="email" required/>
        <Label htmlFor="password">Password</Label>
        <Input type="password" id="password" name="password" required/>
        <Button variant="primary" type="submit">Submit</Button>
        <br />
        <h2>Hi {emailAddrs}</h2>
        <h2>Your password is {password}</h2>
      </Form>
    </Container>
  );
};

export default Newpage;
